import React, { Component } from "react";
import { connect } from "react-redux";
import { Redirect } from "react-router-dom";
import { loginUser } from "../actions/loginUser"

class Login extends Component {
    state = { email: "", password: "" };
    handleEmailChange = ({ target }) => {
      this.setState({ email: target.value });
    };
    handlePasswordChange = ({ target }) => {
      this.setState({ password: target.value });
    };
    handleSubmit = () => {
      const { dispatch } = this.props;
      const { email, password } = this.state;
      dispatch(loginUser(email, password));
    };
    render() {
      const { loginError, isAuthenticated } = this.props;
      if (isAuthenticated) {
        return <Redirect to="/aboutus" />;
      } else {
        return (
          <div className="container"><br></br><br></br>
            <h1 className="text-center title-page">Log In</h1>
            <div className="login-form">
              <form id="customer-form" action="#" method="post">
                <div>
                  <input type="hidden" name="back" defaultValue="my-account" />
                  <div className="form-group no-gutters">
                    <input className="form-control" id="input-email" name="email" type="email" placeholder=" Email" onChange={this.handleEmailChange}/>
                  </div>
                  <div className="form-group no-gutters">
                    <div className="input-group js-parent-focus">
                      <input className="form-control js-child-focus js-visible-password" name="password" type="password" defaultValue placeholder="Password" />
                    </div>
                  </div>
                  <div className="no-gutters text-center">
                    <div className="forgot-password">
                      <a href="user-reset-password.html" rel="nofollow">
                        Forgot your password?
                      </a>
                    </div>
                  </div>
                </div>
                <div className="clearfix">
                  <div className="text-center no-gutters">
                    <input type="hidden" name="submitLogin" defaultValue={1} /><br></br>
                    <button className="btn btn-primary" data-link-action="sign-in" type="submit">
                      Sign in
                    </button>
                  </div><br></br><br></br>
                </div>
              </form>
            </div>
          </div>

        );
      }
    }
  }
  
  function mapStateToProps(state) {
    return {
      isLoggingIn: state.auth.isLoggingIn,
      loginError: state.auth.loginError,
      isAuthenticated: state.auth.isAuthenticated,
    };
  }
  export default connect(mapStateToProps)(Login);
