import React, {Component} from 'react';
import firebase from "firebase";
import firebaseConfig from '../firebase/config';

class Header extends Component{
    constructor(props) {
        super(props);
        if (!firebase.apps.length) {
          firebase.initializeApp(firebaseConfig);
        }
        this.state = {
          listCart: [],
          productCart: [],
          totalPrice: 0,
        };
    }
    fetchCart = () => {
        const cartref = firebase.database().ref(`/carts/nabil@gmail`);
        const productref = firebase.database().ref("listProduct");
        cartref.on("value", (snapshot) => {
            let carts = snapshot.val();
            if(carts !== null){
                this.setState({listCart: carts});
            }
            productref.on("value", (productSnapshot) => {
                console.log(productSnapshot);
                let products = productSnapshot.val();
                let newProductCart = [];
                if(products != null && carts != null){
                    let total = 0;
                    products.map((item) => {
                        carts.products.map((cart) => {
                            if(item.id === cart.id){
                                newProductCart.push(this.itemCart(item, cart));
                                this.setState({
                                    productCart: newProductCart,
                                    loadingCart: false,
                                });
                                total += item.price * cart.qty;
                            }
                        });
                    });
                    this.countTotalPrice(total);
                }else{
                    this.setState({productCart: [], totalPrice: 0, loadingCart: false});
                }
                this.getImage();
            });
        });
    }
    componentDidMount() {
        console.log(this.props);
        this.fetchCart();
    }

    deleteCart = (productID) => {
        let newCarts = this.state.listCart.products.filter((item) => {
            return item.id !== productID;
        });
        console.log(this.state.productCart);
        firebase.database().ref("carts/nabil@gmail.com/products").set(newCarts);
    }
    itemCart = (product, cart) => {
        let newCart = {
            ...product,
            ...cart,
        };
        return newCart;
    }
    countTotalPrice = (price) => {
        this.setState({totalPrice: price});
    }
    getImage = () => {
        const storage = firebase.storage();
        this.state.productCart.map((item) => {
            storage
                .ref(`/product-image/${item.image}`)
                .getDownloadURL()
                .then((link) => {
                    let newCart = this.state.productCart.map((cart) => {
                        if(item.id === cart.id){
                            cart.image = link;
                        }
                        return cart;
                    });
                    this.setState({productCart: newCart});
                });
        });
    }
    render(){
        return(
            <header id="header">
                {/* header left mobie */}
                <div className="header-mobile d-md-none">
                    <div className="mobile hidden-md-up text-xs-center d-flex align-items-center justify-content-around">
                    {/* menu left */}
                    <div id="mobile_mainmenu" className="item-mobile-top">
                        <i className="fa fa-bars" aria-hidden="true" />
                    </div>
                    {/* logo */}
                    <div className="mobile-logo">
                        <a href="/">
                        <img className="logo-mobile img-fluid" src="img/home/logo-mobie.png" alt="Prestashop_Furnitica" />
                        </a>
                    </div>
                    {/* menu right */}
                    <div className="mobile-menutop" data-target="#mobile-pagemenu">
                        <i className="zmdi zmdi-more" />
                    </div>
                    </div>
                </div>
                {/* header desktop */}
                <div className="header-top d-xs-none ">
                    <div className="container">
                    <div className="row">
                        {/* logo */}
                        <div className="col-sm-2 col-md-2 d-flex align-items-center">
                        <div id="logo">
                            <a href="/">
                            <img src="img/home/logo.png" alt="logo" className="img-fluid" />
                            </a>
                        </div>
                        </div>
                        {/* menu */}
                        <div className="col-sm-5 col-md-5 align-items-center justify-content-center navbar-expand-md main-menu">
                        <div className="menu navbar collapse navbar-collapse">
                            <ul className="menu-top navbar-nav">
                            <li>
                                <a href="/" className="parent">Home</a>
                            </li>
                            <li>
                                <a href="/product" className="parent">Product</a>
                            </li>
                            </ul>
                        </div>
                        </div>
                        <div className="col-sm-5 col-md-5 d-flex align-items-center justify-content-end" id="search_widget">
                        <div id="block_myaccount_infos" className="hidden-sm-down dropdown">
                            <div className="myaccount-title ">
                            <a href="/login" data-toggle="collapse" className="acount">
                                <i className="fa fa-user" aria-hidden="true" />
                            </a>
                            </div>
                        </div>
                        <div className="desktop_cart">
                            <div className="blockcart block-cart cart-preview tiva-toggle">
                            <div className="header-cart tiva-toggle-btn">
                                {/* {this.state.productCart.length > 0 && (
                                    <span className="total-count">
                                    {this.state.productCart.length}
                                    </span>
                                )} */}
                                <i className="fa fa-shopping-cart" aria-hidden="true" />
                            </div>
                            <div className="dropdown-content">
                                <div className="cart-content">
                                <table>
                                    <tbody>
                                    {this.state.productCart.length === 0 && (
                                    <li className="text-center">
                                        <h4>Empty</h4>
                                    </li>
                                    )}
                                    {this.state.productCart.map((item) => {
                                    <tr>
                                        <td className="product-image">
                                        <a href="product-detail.html">
                                            <img src={item.image} alt="Product" />
                                        </a>
                                        </td>
                                        <td>
                                        <div className="product-name">
                                            <a href="#">{item.name}</a>
                                        </div>
                                        <div>
                                            {item.qty}x -{" "}
                                            <span className="product-price">Rp {item.qty * item.price}</span>
                                        </div>
                                        </td>
                                        <td className="action">
                                        <a className="remove" href="#">
                                            <i className="fa fa-trash-o" aria-hidden="true"
                                            onClick={() => this.deleteCart(item.id)} />
                                        </a>
                                        </td>
                                    </tr>
                                    })}
                                    <tr className="total">
                                        <td >Total:</td>
                                        <td> Rp {this.state.totalprice}</td>
                                    </tr>
                                    <tr>
                                        <td colSpan={3} className="d-flex justify-content-center">
                                        <div className="cart-button">
                                            <a href="/cart" title="View Cart">View Cart</a>
                                            <a href="/checkout" title="Checkout">Checkout</a>
                                        </div>
                                        </td>
                                    </tr>
                                    </tbody>
                                </table>
                                </div>
                            </div>
                            </div>
                        </div>
                        </div>

                    </div>
                    </div>
                </div>
                {/* main content */}
                <div className="main-content">
                    <div className="wrap-banner">
                    {/* breadcrumb */}
                    <nav className="breadcrumb-bg">
                        <div className="container no-index">
                        <div className="breadcrumb">
                            <br></br>
                        </div>
                        </div>
                    </nav>
                    </div>
                    <div id="wrapper-site">
                        <div id="content-wrapper" class="full-width">
                            <div id="main">
                                <br></br><br></br>
                            </div>
                        </div>
                    </div>
                </div>
            </header> 
        );
    }
} export default Header
